import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

class RealEstateService {
  async searchProperties(query) {
    try {
      const response = await axios.post(`${API_BASE_URL}/search`, { query });
      return response.data;
    } catch (error) {
      console.error('Error searching properties:', error);
      return {
        status: 'error',
        message: error.response?.data?.error || 'Failed to search properties. Please try again.',
        properties: []
      };
    }
  }

  async getPropertyAmenities(propertyId) {
    try {
      const response = await axios.get(`${API_BASE_URL}/property/${propertyId}/amenities`);
      return response.data;
    } catch (error) {
      console.error('Error fetching amenities:', error);
      throw error;
    }
  }

  async negotiatePrice(propertyId, offer) {
    try {
      const response = await axios.post(`${API_BASE_URL}/property/${propertyId}/negotiate`, { offer });
      return response.data;
    } catch (error) {
      console.error('Error negotiating price:', error);
      throw error;
    }
  }

  async finalizeDeal(propertyId) {
    try {
      const response = await axios.post(`${API_BASE_URL}/property/${propertyId}/finalize`);
      return response.data;
    } catch (error) {
      console.error('Error finalizing deal:', error);
      throw error;
    }
  }
}

const realEstateService = new RealEstateService();
export default realEstateService;
