// Enhanced ChatInterface.jsx
import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
  Container,
  CircularProgress,
  Card,
  CardContent,
  CardActions,
  Chip,
  Divider,
  Alert,
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Tooltip,
  Snackbar
} from '@mui/material';
import {
  Home as HomeIcon,
  LocationOn as LocationIcon,
  AttachMoney as MoneyIcon,
  Bed as BedIcon,
  Bathtub as BathIcon,
  SquareFoot as AreaIcon,
  Pets as PetIcon,
  Star as StarIcon,
  Send as SendIcon,
  SmartHome as SmartHomeIcon,
  Security as SecurityIcon,
  FitnessCenter as GymIcon,
  School as SchoolIcon,
  LocalHospital as HospitalIcon,
  CheckCircle as CheckIcon,
  Info as InfoIcon,
  Warning as WarningIcon
} from '@mui/icons-material';
import RealEstateService from '../services/realEstateService';

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [properties, setProperties] = useState([]);
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [showPropertyDetails, setShowPropertyDetails] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'info' });
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Add enhanced welcome message
    setMessages([
      {
        id: 1,
        text: "👋 Welcome to AI Real Estate Assistant! I'm here to help you find your perfect home.\n\n🏠 I can help you with:\n• Property searches with smart filtering\n• Detailed amenities information\n• Price negotiations\n• Deal closing process\n\nTry asking: 'Find 2BHK apartments in San Francisco under $500K'",
        sender: 'agent',
        timestamp: new Date().toISOString(),
        type: 'welcome'
      },
    ]);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = {
      id: messages.length + 1,
      text: input,
      sender: 'user',
      timestamp: new Date().toISOString(),
    };

    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      // Use the main chat endpoint for all queries
      const response = await RealEstateService.chatQuery(input);
      
      const agentMessage = {
        id: messages.length + 2,
        text: response.message || "I'm processing your request...",
        sender: 'agent',
        timestamp: new Date().toISOString(),
        data: response.data,
        status: response.status
      };

      setMessages((prev) => [...prev, agentMessage]);

      // Handle properties data
      if (response.data?.properties && response.data.properties.length > 0) {
        setProperties(response.data.properties.map(p => ({ 
          ...p, 
          showAmenities: false,
          negotiationStatus: 'none' 
        })));
      }

      // Show success/error snackbar
      if (response.status === 'error') {
        setSnackbar({ 
          open: true, 
          message: 'Something went wrong. Please try again.', 
          severity: 'error' 
        });
      }

    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        id: messages.length + 2,
        text: '❌ Sorry, I encountered an error. Please try again or rephrase your question.',
        sender: 'agent',
        timestamp: new Date().toISOString(),
        status: 'error'
      };
      setMessages((prev) => [...prev, errorMessage]);
      
      setSnackbar({ 
        open: true, 
        message: 'Connection error. Please check your internet and try again.', 
        severity: 'error' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handlePropertyAction = async (property, action, data = {}) => {
    try {
      let response;
      
      switch (action) {
        case 'showAmenities':
          response = await RealEstateService.getPropertyAmenities(property.id);
          if (response.status === 'success') {
            setProperties(prev => prev.map(p =>
              p.id === property.id
                ? { ...p, showAmenities: !p.showAmenities, amenitiesData: response.data }
                : p
            ));
          }
          break;
          
        case 'makeOffer':
          const suggestedOffer = Math.round(property.price * 0.95);
          setInput(`I want to make an offer of $${suggestedOffer.toLocaleString()} for property ${property.id}`);
          break;
          
        case 'negotiate':
          response = await RealEstateService.negotiatePrice(property.id, data.offer);
          if (response.status === 'success') {
            setProperties(prev => prev.map(p =>
              p.id === property.id
                ? { ...p, negotiationStatus: response.data.negotiation_status, negotiationData: response.data }
                : p
            ));
            
            const negotiationMessage = {
              id: messages.length + 1,
              text: response.message,
              sender: 'agent',
              timestamp: new Date().toISOString(),
              data: response.data,
              type: 'negotiation'
            };
            setMessages(prev => [...prev, negotiationMessage]);
          }
          break;
          
        case 'closeDeal':
          response = await RealEstateService.finalizeDeal(property.id);
          if (response.status === 'success') {
            setProperties(prev => prev.map(p =>
              p.id === property.id
                ? { ...p, dealStatus: 'closed', closingData: response.data }
                : p
            ));
            
            const closingMessage = {
              id: messages.length + 1,
              text: response.message,
              sender: 'agent',
              timestamp: new Date().toISOString(),
              data: response.data,
              type: 'closing'
            };
            setMessages(prev => [...prev, closingMessage]);
            
            setSnackbar({ 
              open: true, 
              message: '🎉 Congratulations! Deal successfully closed!', 
              severity: 'success' 
            });
          }
          break;
          
        case 'viewDetails':
          setSelectedProperty(property);
          setShowPropertyDetails(true);
          break;
      }
    } catch (error) {
      console.error('Property action error:', error);
      setSnackbar({ 
        open: true, 
        message: 'Action failed. Please try again.', 
        severity: 'error' 
      });
    }
  };

  const getMessageIcon = (message) => {
    if (message.type === 'welcome') return <HomeIcon color="primary" />;
    if (message.type === 'negotiation') return <MoneyIcon color="secondary" />;
    if (message.type === 'closing') return <CheckIcon color="success" />;
    if (message.status === 'error') return <WarningIcon color="error" />;
    return message.sender === 'user' ? <Avatar sx={{ bgcolor: 'primary.main', width: 32, height: 32 }}>U</Avatar> : <Avatar sx={{ bgcolor: 'secondary.main', width: 32, height: 32 }}>AI</Avatar>;
  };

  const PropertyCard = ({ property }) => (
    <Card 
      sx={{ 
        mb: 2, 
        borderLeft: property.dealStatus === 'closed' ? '4px solid #4caf50' : '4px solid #2196f3',
        opacity: property.dealStatus === 'closed' ? 0.8 : 1
      }}
    >
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            {property.address}
          </Typography>
          {property.dealStatus === 'closed' && (
            <Chip label="SOLD" color="success" size="small" />
          )}
        </Box>
        
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <LocationIcon sx={{ mr: 1, color: 'text.secondary' }} />
          <Typography color="text.secondary">
            {property.city}, {property.state}
          </Typography>
        </Box>

        <Grid container spacing={2} sx={{ mb: 2 }}>
          <Grid item xs={6} sm={3}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <MoneyIcon sx={{ mr: 1, color: 'success.main' }} />
              <Typography variant="h6" color="success.main">
                ${property.price.toLocaleString()}
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={6} sm={3}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <BedIcon sx={{ mr: 1 }} />
              <Typography>{property.bedrooms} bed</Typography>
            </Box>
          </Grid>
          <Grid item xs={6} sm={3}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <BathIcon sx={{ mr: 1 }} />
              <Typography>{property.bathrooms} bath</Typography>
            </Box>
          </Grid>
          <Grid item xs={6} sm={3}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <AreaIcon sx={{ mr: 1 }} />
              <Typography>{property.square_feet?.toLocaleString()} sqft</Typography>
            </Box>
          </Grid>
        </Grid>

        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
          <Chip 
            label={property.property_type} 
            variant="outlined" 
            size="small" 
          />
          {property.is_pet_friendly && (
            <Chip 
              label="Pet Friendly" 
              icon={<PetIcon />} 
              variant="outlined" 
              size="small" 
              color="secondary"
            />
          )}
          {property.year_built && (
            <Chip 
              label={`Built ${property.year_built}`} 
              variant="outlined" 
              size="small" 
            />
          )}
        </Box>

        {property.showAmenities && property.amenitiesData && (
          <Box sx={{ mt: 2, p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
              🏢 Property Amenities:
            </Typography>
            {property.amenitiesData.amenities && (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {property.amenitiesData.amenities.map((amenity, index) => (
                  <Chip 
                    key={index} 
                    label={amenity} 
                    size="small" 
                    variant="outlined"
                  />
                ))}
              </Box>
            )}
          </Box>
        )}

        {property.negotiationData && (
          <Alert 
            severity={property.negotiationData.negotiation_status === 'accepted' ? 'success' : 'info'}
            sx={{ mt: 2 }}
          >
            <strong>Negotiation Update:</strong> {property.negotiationData.message || 'Offer under review'}
          </Alert>
        )}
      </CardContent>

      <CardActions sx={{ flexWrap: 'wrap', gap: 1 }}>
        <Button
          size="small"
          onClick={() => handlePropertyAction(property, 'viewDetails')}
          startIcon={<InfoIcon />}
        >
          View Details
        </Button>
        
        <Button
          size="small"
          onClick={() => handlePropertyAction(property, 'showAmenities')}
          startIcon={<SmartHomeIcon />}
        >
          {property.showAmenities ? 'Hide Amenities' : 'Show Amenities'}
        </Button>

        {property.dealStatus !== 'closed' && (
          <>
            <Button
              size="small"
              variant="contained"
              onClick={() => handlePropertyAction(property, 'makeOffer')}
              startIcon={<MoneyIcon />}
            >
              Make Offer
            </Button>

            {property.negotiationStatus === 'accepted' && (
              <Button
                size="small"
                color="success"
                variant="contained"
                onClick={() => handlePropertyAction(property, 'closeDeal')}
                startIcon={<CheckIcon />}
              >
                Close Deal
              </Button>
            )}
          </>
        )}
      </CardActions>
    </Card>
  );

  return (
    <Container maxWidth="lg">
      <Box sx={{ height: '100vh', py: 2, display: 'flex', flexDirection: 'column' }}>
        <Paper 
          elevation={3} 
          sx={{ 
            flexGrow: 1, 
            display: 'flex', 
            flexDirection: 'column',
            borderRadius: 2,
            overflow: 'hidden'
          }}
        >
          {/* Header */}
          <Box sx={{ 
            p: 2, 
            bgcolor: 'primary.main', 
            color: 'white',
            display: 'flex',
            alignItems: 'center'
          }}>
            <HomeIcon sx={{ mr: 2 }} />
            <Typography variant="h6">
              AI Real Estate Assistant
            </Typography>
          </Box>

          {/* Messages Area */}
          <Box sx={{ 
            flexGrow: 1, 
            overflow: 'auto', 
            p: 2,
            display: 'flex',
            flexDirection: 'column'
          }}>
            {messages.map((message) => (
              <Box
                key={message.id}
                sx={{
                  display: 'flex',
                  justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                  mb: 2,
                  alignItems: 'flex-start'
                }}
              >
                {message.sender === 'agent' && (
                  <Box sx={{ mr: 1, mt: 0.5 }}>
                    {getMessageIcon(message)}
                  </Box>
                )}
                
                <Paper
                  sx={{
                    p: 2,
                    backgroundColor: message.sender === 'user' 
                      ? 'primary.light' 
                      : message.status === 'error' 
                        ? 'error.light'
                        : 'grey.100',
                    color: message.sender === 'user' ? 'white' : 'text.primary',
                    maxWidth: '80%',
                    borderRadius: 2,
                    whiteSpace: 'pre-line'
                  }}
                >
                  <Typography variant="body1">
                    {message.text}
                  </Typography>
                  
                  {message.data?.search_criteria && (
                    <Box sx={{ mt: 1, p: 1, bgcolor: 'rgba(255,255,255,0.1)', borderRadius: 1 }}>
                      <Typography variant="caption">
                        Applied filters: {Object.entries(message.data.search_criteria).map(([key, value]) => 
                          `${key}: ${value}`
                        ).join(', ')}
                      </Typography>
                    </Box>
                  )}
                </Paper>

                {message.sender === 'user' && (
                  <Box sx={{ ml: 1, mt: 0.5 }}>
                    {getMessageIcon(message)}
                  </Box>
                )}
              </Box>
            ))}
            <div ref={messagesEndRef} />
          </Box>

          {/* Properties Display */}
          {properties.length > 0 && (
            <Box sx={{ maxHeight: '400px', overflow: 'auto', px: 2, pb: 2 }}>
              <Divider sx={{ mb: 2 }} />
              <Typography variant="h6" sx={{ mb: 2 }}>
                🏠 Found Properties ({properties.length}):
              </Typography>
              {properties.map((property) => (
                <PropertyCard key={property.id} property={property} />
              ))}
            </Box>
          )}

          {/* Input Area */}
          <Box sx={{ p: 2, borderTop: '1px solid', borderColor: 'divider' }}>
            <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 1 }}>
              <TextField
                fullWidth
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Ask me about properties, amenities, negotiations, or closing deals..."
                disabled={loading}
                variant="outlined"
                size="small"
                multiline
                maxRows={3}
              />
              <Button 
                type="submit" 
                variant="contained" 
                disabled={loading || !input.trim()}
                sx={{ minWidth: 'auto', px: 2 }}
              >
                {loading ? <CircularProgress size={24} /> : <SendIcon />}
              </Button>
            </Box>
            
            {/* Quick Actions */}
            <Box sx={{ mt: 1, display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              <Chip 
                label="Search Properties" 
                size="small" 
                clickable 
                onClick={() => setInput("Find 2BHK apartments under $500K")}
              />
              <Chip 
                label="Pet-Friendly Options" 
                size="small" 
                clickable 
                onClick={() => setInput("Show pet-friendly properties")}
              />
              <Chip 
                label="Luxury Properties" 
                size="small" 
                clickable 
                onClick={() => setInput("Find luxury condos with amenities")}
              />
            </Box>
          </Box>
        </Paper>

        {/* Property Details Modal */}
        <Dialog 
          open={showPropertyDetails} 
          onClose={() => setShowPropertyDetails(false)}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            Property Details
          </DialogTitle>
          <DialogContent>
            {selectedProperty && (
              <Box>
                <Typography variant="h6">{selectedProperty.address}</Typography>
                <Typography color="text.secondary" sx={{ mb: 2 }}>
                  {selectedProperty.city}, {selectedProperty.state}
                </Typography>
                
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <List dense>
                      <ListItem>
                        <ListItemIcon><MoneyIcon /></ListItemIcon>
                        <ListItemText 
                          primary="Price" 
                          secondary={`$${selectedProperty.price.toLocaleString()}`}
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon><BedIcon /></ListItemIcon>
                        <ListItemText 
                          primary="Bedrooms" 
                          secondary={selectedProperty.bedrooms}
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon><BathIcon /></ListItemIcon>
                        <ListItemText 
                          primary="Bathrooms" 
                          secondary={selectedProperty.bathrooms}
                        />
                      </ListItem>
                    </List>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <List dense>
                      <ListItem>
                        <ListItemIcon><AreaIcon /></ListItemIcon>
                        <ListItemText 
                          primary="Square Feet" 
                          secondary={selectedProperty.square_feet?.toLocaleString()}
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon><HomeIcon /></ListItemIcon>
                        <ListItemText 
                          primary="Property Type" 
                          secondary={selectedProperty.property_type}
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon><PetIcon /></ListItemIcon>
                        <ListItemText 
                          primary="Pet Friendly" 
                          secondary={selectedProperty.is_pet_friendly ? 'Yes' : 'No'}
                        />
                      </ListItem>
                    </List>
                  </Grid>
                </Grid>
              </Box>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setShowPropertyDetails(false)}>
              Close
            </Button>
          </DialogActions>
        </Dialog>

        {/* Snackbar for notifications */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={6000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
        >
          <Alert 
            onClose={() => setSnackbar({ ...snackbar, open: false })} 
            severity={snackbar.severity}
            sx={{ width: '100%' }}
          >
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Box>
    </Container>
  );
};

export default ChatInterface;
